const INITIAL_STATE = {
    company: {
        _id: '',
    },
}

export default function account(state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'UPDATE_ACCOUNT':
            return {
                ...state,
                ...action.payload
            }
        case 'DELETE_ACCOUNT':
            return INITIAL_STATE
        default:
            return state
    }
}