const INITIAL_STATE = {
    longitude: null, latitude: null
}

export default function account(state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'UPDATE_COORDINATE':
            return {
                ...state,
                ...action.payload
            }
        case 'DELETE_COORDINATE':
            return INITIAL_STATE
        default:
            return state
    }
}