const INITIAL_STATE = {
    wechat: {},
    _id: '',
    openid: '',
    session_key: '',
}

export default function userInfo(state = INITIAL_STATE, action) {
    switch (action.type) {
        case 'UPDATE_USERINFO':
            return {
                ...state,
                ...action.payload
            }
        default:
            return state
    }
}